// Parse Groq classification and map to Airtable fields
let category = 'unknown';
let confidence = 0;
try {
  const content = $json.choices[0].message.content;
  const parsed = JSON.parse(content.replace(/```json?\n?/g, '').replace(/```/g, '').trim());
  category = parsed.category || 'unknown';
  confidence = parsed.confidence || 0;
} catch(e) {
  category = 'unknown';
  confidence = 0;
}

// Map category to Airtable Status and Reply Category
const statusMap = {
  'interested': { status: 'Interested', reply: 'Ready to Schedule' },
  'more_info': { status: 'Replied', reply: 'More Info' },
  'not_interested': { status: 'Not Interested', reply: 'Not Interested' },
  'out_of_office': { status: 'Replied', reply: 'Out of Office' },
  'unsubscribe': { status: 'Do Not Contact', reply: 'Unsubscribe' }
};

const mapped = statusMap[category] || { status: 'Replied', reply: 'Unknown' };

// Get record ID from Find Lead
const findLeadData = $('Find Lead').item.json;
const recordId = (findLeadData.records && findLeadData.records.length > 0) ? findLeadData.records[0].id : '';

return { json: { category, confidence, airtableStatus: mapped.status, replyCategory: mapped.reply, recordId } };
