// Parse incoming leads array, normalize fields
const body = $input.first().json.body || $input.first().json;
const leads = body.leads || [];

if (!leads.length) {
  return [{ json: { error: 'No leads in payload', created: 0, skipped: 0 } }];
}

// Normalize each lead to match Airtable field names
const normalized = leads.map(lead => ({
  json: {
    'First Name': lead.first_name || lead.firstName || '',
    'Full Name': lead.full_name || lead.fullName || '',
    'Email Address': lead.email || lead.emailAddress || '',
    'Company Name': lead.company_name || lead.companyName || '',
    'LinkedIn URL': lead.linkedin_url || lead.linkedinUrl || '',
    'LinkedIn Organization URL': lead.linkedin_org_url || lead.linkedinOrgUrl || '',
    'Company Website': lead.company_website || lead.companyWebsite || '',
    'Location': lead.location || '',
    'Title': lead.title || '',
    '_totalLeads': leads.length
  }
}));

return normalized;
